<template>
  <ion-page>
    <ion-header>
      <ion-toolbar>
        <ion-title>IONIC STORE</ion-title>
      </ion-toolbar>
    </ion-header>
    <ion-content>
      <ion-grid>
        <ion-row>
          <ion-col size="6" v-for="product in products" :key="product.id">
            <ion-card>
              <img :src="product.image" style="width:200px; height:150px; " alt="Product Image"  />
              <ion-card-content>
                <ion-card-title style="font-size:16px" >{{ product.name }}</ion-card-title>
                <ion-card-title style="font-size:16px" > ${{ product.price }}</ion-card-title>
                <ion-button expand="full" @click="handleAddToCart(product)"  style="font-size:9px; "> 
                  Add To Cart
                </ion-button>
              </ion-card-content>
            </ion-card>
          </ion-col>
        </ion-row>
      </ion-grid>
      <ion-alert
        :is-open="showAlert"
        header="Product Added"
        :message="alertMessage"
        buttons="OK"
        @didDismiss="showAlert = false"
      ></ion-alert>
    </ion-content>
  </ion-page>
</template>

<script setup>
import { ref } from 'vue';
import { useStore } from '../store';
import {
  IonPage,
  IonHeader,
  IonToolbar,
  IonTitle,
  IonContent,
  IonGrid,
  IonRow,
  IonCol,
  IonCard,
  IonCardContent,
  IonCardTitle,
  IonButton,
  IonAlert,
  IonIcon
} from '@ionic/vue';

const products = ref([
  { id: 1, price:10, name: 'Nike Air Jordan', image: 'data:image/jpeg;base64,' },
  { id: 2, price:120, name: 'Air Jordan 4', image: 'data:image/jpeg;base64,' },
  { id: 3, price:39, name: 'Nice White Hoodie', image: 'data:image/jpeg;base64,' },
  { id: 4, price:79, name: 'Brogues Men Shoe', image: 'data:image/jpeg;base64,' },
  { id: 5, price:100, name: 'Lenovo Ideapad', image: 'data:image/jpeg;base64,'},
]);

const showAlert = ref(false);
const alertMessage = ref('');
const { addToCheckout } = useStore();

const handleAddToCart = (product) => {
  addToCheckout(product);
  alertMessage.value = `${product.name} has been added to your cart.`;
  showAlert.value = true;
};
</script>